import React, { useEffect, useState } from "react";
import { getEmployees, deleteEmployee } from "../services/employeeService";
import { Link } from "react-router-dom";

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);

  const fetchEmployees = () => {
    getEmployees().then((res) => setEmployees(res.data));
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this employee?")) {
      deleteEmployee(id).then(() => fetchEmployees());
    }
  };

  return (
    <div>
      <h2>Employees</h2>
      <Link to="/employee/add" className="btn btn-primary mb-2">Add Employee</Link>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Email</th><th>Phone</th><th>Salary</th><th>Job Title</th><th>Department</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((emp) => (
            <tr key={emp.id}>
              <td>{emp.id}</td>
              <td>{emp.name}</td>
              {/* <td>{emp.email}</td>
              <td>{emp.phone}</td>
              <td>{emp.salary}</td>
              <td>{emp.jobTitle}</td>
              <td>{emp.department?.name}</td> */}
              <td>
                <Link to={`/employee/edit/${emp.id}`} className="btn btn-warning btn-sm">Edit</Link>
                <button onClick={() => handleDelete(emp.id)} className="btn btn-danger btn-sm ml-2">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EmployeeList;
