import React, { useEffect, useState } from "react";
import { getAllDepartments, deleteDepartment } from "../services/departmentService";

export default function DepartmentList({ onEdit }) {
    const [departments, setDepartments] = useState([]);

    useEffect(() => {
        fetchDepartments();
    }, []);

    const fetchDepartments = async () => {
        const response = await getAllDepartments();
        setDepartments(response.data);
    };

    const handleDelete = async (id) => {
        await deleteDepartment(id);
        fetchDepartments();
    };

    return (
        <div>
            <h2>Department List</h2>
            <table border="1">
                <thead>
                    <tr>
                        <th>Name</th><th>Location</th><th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {departments.map((dept) => (
                        <tr key={dept.id}>
                            <td>{dept.name}</td>
                            <td>{dept.location}</td>
                            <td>
                                <button onClick={() => onEdit(dept)}>Edit</button>
                                <button onClick={() => handleDelete(dept.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}
