import React, { useState, useEffect } from "react";
import { addEmployee, updateEmployee } from "../services/employeeService";

export default function EmployeeForm({ selected, onSave }) {
    const [employee, setEmployee] = useState({
        name: "",
        email: "",
        phone: "",
        jobTitle: "",
        salary: ""
    });

    useEffect(() => {
        if (selected) setEmployee(selected);
    }, [selected]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployee({ ...employee, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (employee.employeeId) {
            await updateEmployee(employee.employeeId, employee);
        } else {
            await addEmployee(employee);
        }
        setEmployee({ name: "", email: "", phone: "", jobTitle: "", salary: "" });
        onSave();
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>{employee.employeeId ? "Edit" : "Add"} Employee</h2>
            <input name="name" value={employee.name} onChange={handleChange} placeholder="Name" required />
            <input name="email" value={employee.email} onChange={handleChange} placeholder="Email" required />
            <input name="phone" value={employee.phone} onChange={handleChange} placeholder="Phone" required />
            <input name="jobTitle" value={employee.jobTitle} onChange={handleChange} placeholder="Job Title" required />
            <input name="salary" value={employee.salary} onChange={handleChange} placeholder="Salary" required />
            <button type="submit">Save</button>
        </form>
    );
}
