import React, { useEffect, useState } from "react";
import { addEmployee, getEmployee, updateEmployee } from "../services/employeeService";
import { getDepartments } from "../services/departmentService";
import { useNavigate, useParams } from "react-router-dom";

const EmployeeForm = () => {
  const [employee, setEmployee] = useState({ name: "", email: "", phone: "", salary: "", jobTitle: "", department: { id: "" } });
  const [departments, setDepartments] = useState([]);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    getDepartments().then((res) => setDepartments(res.data));
    if (id) {
      getEmployee(id).then((res) => setEmployee(res.data));
    }
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "department") {
      setEmployee({ ...employee, department: { id: value } });
    } else {
      setEmployee({ ...employee, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (id) {
      updateEmployee(id, employee).then(() => navigate("/employees"));
    } else {
      addEmployee(employee).then(() => navigate("/employees"));
    }
  };

  return (
    <div>
      <h2>{id ? "Edit Employee" : "Add Employee"}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name</label>
          <input name="name" value={employee.name} onChange={handleChange} className="form-control" required />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input name="email" type="email" value={employee.email} onChange={handleChange} className="form-control" required />
        </div>
        <div className="form-group">
          <label>Phone</label>
          <input name="phone" value={employee.phone} onChange={handleChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>Salary</label>
          <input name="salary" type="number" value={employee.salary} onChange={handleChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>Job Title</label>
          <input name="jobTitle" value={employee.jobTitle} onChange={handleChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>Department</label>
          <select name="department" value={employee.department.id} onChange={handleChange} className="form-control" required>
            <option value="">Select Department</option>
            {departments.map((dept) => (
              <option key={dept.id} value={dept.id}>{dept.name}</option>
            ))}
          </select>
        </div>
        <button type="submit" className="btn btn-success">{id ? "Update" : "Add"}</button>
      </form>
    </div>
  );
};

export default EmployeeForm;
