import React, { useEffect, useState } from "react";
import { addDepartment, updateDepartment } from "../services/departmentService";

export default function DepartmentForm({ selected, onSave }) {
    const [department, setDepartment] = useState({ name: "", location: "" });

    useEffect(() => {
        if (selected) setDepartment(selected);
    }, [selected]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setDepartment({ ...department, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (department.id) {
            await updateDepartment(department.id, department);
        } else {
            await addDepartment(department);
        }
        setDepartment({ name: "", location: "" });
        onSave();
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>{department.id ? "Edit" : "Add"} Department</h2>
            <input name="name" value={department.name} onChange={handleChange} placeholder="Name" required />
            <input name="location" value={department.location} onChange={handleChange} placeholder="Location" required />
            <button type="submit">Save</button>
        </form>
    );
}
