import React, { useState } from "react";
import EmployeeList from "./components/EmployeeList";
import EmployeeForm from "./components/EmployeeForm";
import DepartmentList from "./components/DepartmentList";
import DepartmentForm from "./components/DepartmentForm";
import "bootstrap/dist/css/bootstrap.min.css";


function App() {
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [selectedDepartment, setSelectedDepartment] = useState(null);
    const [refreshEmp, setRefreshEmp] = useState(false);
    const [refreshDept, setRefreshDept] = useState(false);

    return (
        <div>
            <h1>Employee Management System</h1>

            <div style={{ display: "flex", justifyContent: "space-around", flexWrap: "wrap" }}>
                <div>
                    <EmployeeForm selected={selectedEmployee} onSave={() => setRefreshEmp(!refreshEmp)} />
                    <EmployeeList key={refreshEmp} onEdit={setSelectedEmployee} />
                </div>

                <div>
                    <DepartmentForm selected={selectedDepartment} onSave={() => setRefreshDept(!refreshDept)} />
                    <DepartmentList key={refreshDept} onEdit={setSelectedDepartment} />
                </div>
            </div>
        </div>
    );
}

export default App;
