import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function EmployeeList() {
  const [employees, setEmployees] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5275/api/employees')
      .then(res => res.json())
      .then(data => setEmployees(data))
      .catch(console.error);
  }, []);

  const handleDelete = (id) => {
    if (window.confirm('Are you sure to delete this employee?')) {
      fetch(`http://localhost:5275/api/employees/${id}`, {
        method: 'DELETE',
      })
      .then(() => setEmployees(employees.filter(e => e.id !== id)))
      .catch(console.error);
    }
  };

  return (
    <div className="container mt-3">
      <h2>Employees</h2>
      <Link to="/employees/add" className="btn btn-primary mb-2">Add Employee</Link>
      <table className="table table-bordered">
        <thead><tr><th>Name</th><th>Email</th><th>Actions</th></tr></thead>
        <tbody>
          {employees.map(e => (
            <tr key={e.id}>
              <td>{e.name}</td>
              <td>{e.email}</td>
              <td>
                <button onClick={() => navigate(`/employees/edit/${e.id}`)} className="btn btn-sm btn-warning me-2">Edit</button>
                <button onClick={() => handleDelete(e.id)} className="btn btn-sm btn-danger">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EmployeeList;
