import React, { useEffect, useState } from "react";
import { addDepartment, getDepartment, updateDepartment } from "../services/departmentService";
import { useNavigate, useParams } from "react-router-dom";

const DepartmentForm = () => {
  const [department, setDepartment] = useState({ name: "", location: "" });
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      getDepartment(id).then((res) => setDepartment(res.data));
    }
  }, [id]);

  const handleChange = (e) => {
    setDepartment({ ...department, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (id) {
      updateDepartment(id, department).then(() => navigate("/"));
    } else {
      addDepartment(department).then(() => navigate("/"));
    }
  };

  return (
    <div>
      <h2>{id ? "Edit Department" : "Add Department"}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name</label>
          <input type="text" name="name" value={department.name} onChange={handleChange} className="form-control" required />
        </div>
        <div className="form-group">
          <label>Location</label>
          <input type="text" name="location" value={department.location || ""} onChange={handleChange} className="form-control" />
        </div>
        <button type="submit" className="btn btn-success">{id ? "Update" : "Add"}</button>
      </form>
    </div>
  );
};

export default DepartmentForm;
