import React, { useState } from "react";
import EmployeeList from "./components/EmployeeList";
import EmployeeForm from "./components/EmployeeForm";
import DepartmentList from "./components/DepartmentList";
import DepartmentForm from "./components/DepartmentForm";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [refreshEmp, setRefreshEmp] = useState(false);
  const [refreshDept, setRefreshDept] = useState(false);

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Employee Management System</h1>

      <div className="row">
        {/* Employee Section */}
        <div className="col-md-7 mb-4">
          <div className="card shadow mb-4">
            <div className="card-body">
              <h4 className="text-primary">Add / Edit Employee</h4>
              <EmployeeForm
                selected={selectedEmployee}
                onSave={() => setRefreshEmp(!refreshEmp)}
              />
            </div>
          </div>

          <div className="card shadow">
            <div className="card-body">
              <h4 className="text-primary">Employee List</h4>
              <EmployeeList
                key={refreshEmp}
                onEdit={setSelectedEmployee}
              />
            </div>
          </div>
        </div>

        {/* Department Section */}
        <div className="col-md-5 mb-4">
          <div className="card shadow mb-4">
            <div className="card-body">
              <h4 className="text-success">Add / Edit Department</h4>
              <DepartmentForm
                selected={selectedDepartment}
                onSave={() => setRefreshDept(!refreshDept)}
              />
            </div>
          </div>

          <div className="card shadow">
            <div className="card-body">
              <h4 className="text-success">Department List</h4>
              <DepartmentList
                key={refreshDept}
                onEdit={setSelectedDepartment}
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
