import React, { useEffect, useState } from "react";
import { getDepartments, deleteDepartment } from "../services/departmentService";
import { Link } from "react-router-dom";

const DepartmentList = () => {
  const [departments, setDepartments] = useState([]);

  const fetchDepartments = () => {
    getDepartments().then((res) => setDepartments(res.data));
  };

  useEffect(() => {
    fetchDepartments();
  }, []);

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this department?")) {
      deleteDepartment(id).then(() => fetchDepartments());
    }
  };

  return (
    <div>
      <h2>Departments</h2>
      <Link to="/add" className="btn btn-primary">Add Department</Link>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Location</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {departments.map((dept) => (
            <tr key={dept.id}>
              <td>{dept.id}</td>
              <td>{dept.name}</td>
              <td>{dept.location || "-"}</td>
              <td>
                <Link to={`/edit/${dept.id}`} className="btn btn-warning btn-sm">Edit</Link>
                <button onClick={() => handleDelete(dept.id)} className="btn btn-danger btn-sm ml-2">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DepartmentList;
