import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function DepartmentList() {
  const [departments, setDepartments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:8080/api/departments')
      .then(res => res.json())
      .then(data => setDepartments(data))
      .catch(console.error);
  }, []);

  const handleDelete = (id) => {
    if (window.confirm('Are you sure to delete this department?')) {
      fetch(`http://localhost:8080/api/departments/${id}`, {
        method: 'DELETE',
      })
      .then(() => setDepartments(departments.filter(d => d.departmentId !== id)))
      .catch(console.error);
    }
  };

  return (
    <div className="container mt-3">
      <h2>Departments</h2>
      <Link to="/departments/add" className="btn btn-primary mb-2">Add Department</Link>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Name</th>
            <th>Location</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {departments.map(d => (
            <tr key={d.departmentId}>
              <td>{d.name}</td>
              <td>{d.location}</td>
              <td>
                <button
                  onClick={() => navigate(`/departments/edit/${d.departmentId}`)}
                  className="btn btn-sm btn-warning me-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(d.departmentId)}
                  className="btn btn-sm btn-danger"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
          {departments.length === 0 && (
            <tr>
              <td colSpan="3" className="text-center">No departments found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default DepartmentList;
