import React, { useEffect, useState } from "react";
import { getAllDepartments, deleteDepartment } from "../services/departmentService";

export default function DepartmentList({ onEdit }) {
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    fetchDepartments();
  }, []);

  const fetchDepartments = async () => {
    try {
      const response = await getAllDepartments();

      // Debug log to see actual structure
      console.log("API Response:", response.data);

      // ðŸ‘‡ Smart check: if response.data is array use directly
      if (Array.isArray(response.data)) {
        setDepartments(response.data);
      } 
      // ðŸ‘‡ If wrapped inside a field like 'data', unwrap it
      else if (Array.isArray(response.data.data)) {
        setDepartments(response.data.data);
      } 
      else {
        console.error("Unexpected API response format.");
        setDepartments([]);
      }
    } catch (error) {
      console.error("Failed to fetch departments:", error);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this department?")) {
      await deleteDepartment(id);
      fetchDepartments();
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-primary">Department List</h2>
      <table className="table table-bordered table-striped shadow-sm">
        <thead className="table-dark">
          <tr>
            <th>Name</th>
            <th>Location</th>
            <th style={{ width: "160px" }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {departments.length === 0 ? (
            <tr>
              <td colSpan="3" className="text-center">No departments found.</td>
            </tr>
          ) : (
            departments.map((dept) => (
              <tr key={dept.id}>
                <td>{dept.name}</td>
                <td>{dept.location}</td>
                <td>
                  <div className="d-flex gap-2">
                    <button
                      className="btn btn-sm btn-warning"
                      onClick={() => onEdit(dept)}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDelete(dept.id)}
                    >
                      Delete
                    </button>
                  </div>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
